jQuery.fn.extend({jHideAndShowNav:function(e){var t="";var n="."+this.attr("class");var r=$(n+" nav a").length;$(n+" nav a").each(function(){if($(this).index()+1>=e.count){if($(this).index()+1==e.count){if(e.count!=r){t+=$(this).clone().wrap("<p>").parent().html();$(this).css("display","none")}}else{t+=$(this).clone().wrap("<p>").parent().html();$(this).css("display","none")}}});if(r>e.count){$(n+" nav").html($(n+" nav").html()+'<a href="#" class="more-link">'+e.moreText+"</a>")}$(n+" nav .more-link").click(function(){if($(n+" div.hidden-links").css("display")=="none"){$(n+" div.hidden-links").show(e.transitionDuration)}else{$(n+" div.hidden-links").hide(e.transitionDuration)}});$(n+" div.hidden-links").mouseleave(function(){$(this).hide(e.transitionDuration)});$(n+" div.hidden-links").html("<nav>"+t+"</nav>");$(n+" div.hidden-links").delay(e.transitionDuration).hide(e.transitionDuration)}})
